mapper.select:
SELECT id, role_name as roleName, create_date as createDate, note
 FROM t_role
 WHERE id = #{id}

SELECT a.id, a.role_name, create_date, note
FROM t_role a, t_user_role b
WHERE a.id = b.role_id and b.user_id = #{userId}

mapper.update:
UPDATE t_role

    role_name = #{roleName},
    note = #{note}

WHERE id = #{id}
